import { Suspense } from 'react';
import { useParams } from 'react-router-dom';
import {
  useRouteLoaderData,
  json,
  redirect,
  defer,
  Await,
} from 'react-router-dom';

import EventItem from '../components/EventItem';

function EventDetailPage() {
  // usar useRouteLoaderData() cuando tiene ID
  // Clase 330: https://www.udemy.com/course/react-the-complete-guide-incl-redux/learn/lecture/35734014#overview
  const data = useRouteLoaderData('event-detail');

  return (
    <>
      <EventItem event={data.event} />

      {/* <EventItem event={DataTransfer.event} /> */}
    </>
  );
}

export default EventDetailPage;

async function loadEvent(id) {
  const response = await fetch('http://localhost:3002/events/' + id);

  if (!response.ok) {
    throw json(
      { message: 'Could not fetch details for selected event.' },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json();
    return resData;
    // return response;
  }
}

async function loadEvents() {
  const response = await fetch('http://localhost:3002/events');

  if (!response.ok) {
    // return { isError: true, message: 'Could not fetch events.' };
    // throw new Response(JSON.stringify({ message: 'Could not fetch events.' }), {
    //   status: 500,
    // });
    throw json(
      { message: 'Could not fetch events.' },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json();
    return resData.events;
  }
}

export async function loader({ request, params }) {
  const id = params.eventId;
  // return { event: await loadEvent(id) };

  return defer({
    event: await loadEvent(id),
    events: loadEvents(),
  });
}

export async function action({ params, request }) {
  const eventId = params.eventId;
  const response = await fetch('http://localhost:3002/events/' + eventId, {
    method: request.method,
  });

  if (!response.ok) {
    throw json(
      { message: 'Could not delete event.' },
      {
        status: 500,
      }
    );
  }
  return redirect('/events');
}
