  {id: '1', title: 'Evento 1'},
  {id: '2', title: 'Evento 2'},
  {id: '3', title: 'Evento 3'},
  {id: '4', title: 'Evento 4'},
  {id: '5', title: 'Evento 5'},
]

function EventsPage() {
